import React, { useState, useEffect } from 'react';

const Navbar = () => {
  const [scrolled, setScrolled] = useState(false);
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 10) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const toggleMobileMenu = () => {
    setMobileMenuOpen(!mobileMenuOpen);
    document.body.style.overflow = mobileMenuOpen ? 'auto' : 'hidden';
  };

  const closeMobileMenu = () => {
    setMobileMenuOpen(false);
    document.body.style.overflow = 'auto';
  };

  return (
    <nav className={`fixed w-full z-50 transition-all duration-500 ${scrolled ? 'bg-[#0B1F3A]/90 backdrop-blur-md py-2 shadow-xl' : 'bg-transparent py-4'}`}>
      <div className="container mx-auto px-6 flex justify-between items-center">
        <div className="flex items-center">
          <span className="text-white text-3xl font-bold bg-clip-text text-transparent bg-gradient-to-r from-[#1FB8D2] to-white">NovusWave</span>
          <span className="ml-2 text-[#1FB8D2] text-sm hidden md:block">Surfing the Digital Future</span>
        </div>

        <div className="hidden md:flex space-x-8">
          <a href="#home" className="text-white hover:text-[#1FB8D2] transition-colors font-medium relative group">
            <span>Inicio</span>
            <span className="absolute bottom-0 left-0 w-0 h-0.5 bg-[#1FB8D2] transition-all duration-300 group-hover:w-full"></span>
          </a>
          <a href="#about" className="text-white hover:text-[#1FB8D2] transition-colors font-medium relative group">
            <span>Sobre Nosotros</span>
            <span className="absolute bottom-0 left-0 w-0 h-0.5 bg-[#1FB8D2] transition-all duration-300 group-hover:w-full"></span>
          </a>
          <a href="#services" className="text-white hover:text-[#1FB8D2] transition-colors font-medium relative group">
            <span>Servicios</span>
            <span className="absolute bottom-0 left-0 w-0 h-0.5 bg-[#1FB8D2] transition-all duration-300 group-hover:w-full"></span>
          </a>
          <a href="#portfolio" className="text-white hover:text-[#1FB8D2] transition-colors font-medium relative group">
            <span>Portafolio</span>
            <span className="absolute bottom-0 left-0 w-0 h-0.5 bg-[#1FB8D2] transition-all duration-300 group-hover:w-full"></span>
          </a>
          <a href="#contact" className="text-white hover:text-[#1FB8D2] transition-colors font-medium relative group">
            <span>Contacto</span>
            <span className="absolute bottom-0 left-0 w-0 h-0.5 bg-[#1FB8D2] transition-all duration-300 group-hover:w-full"></span>
          </a>
        </div>

        <button 
          className="md:hidden text-white focus:outline-none z-50"
          onClick={toggleMobileMenu}
          aria-label="Toggle menu"
        >
          {mobileMenuOpen ? (
            <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
            </svg>
          ) : (
            <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
            </svg>
          )}
        </button>
      </div>

      {/* Mobile Menu */}
      <div className={`md:hidden fixed inset-0 bg-[#0B1F3A]/95 backdrop-blur-lg z-40 transition-all duration-500 ease-in-out transform ${mobileMenuOpen ? 'translate-x-0' : 'translate-x-full'}`}>
        <div className="container mx-auto px-6 pt-32 h-full flex flex-col">
          <div className="flex flex-col space-y-8 text-center flex-grow">
            <a 
              href="#home" 
              className="text-3xl text-white hover:text-[#1FB8D2] transition-colors py-4"
              onClick={closeMobileMenu}
            >
              Inicio
            </a>
            <a 
              href="#about" 
              className="text-3xl text-white hover:text-[#1FB8D2] transition-colors py-4"
              onClick={closeMobileMenu}
            >
              Sobre Nosotros
            </a>
            <a 
              href="#services" 
              className="text-3xl text-white hover:text-[#1FB8D2] transition-colors py-4"
              onClick={closeMobileMenu}
            >
              Servicios
            </a>
            <a 
              href="#portfolio" 
              className="text-3xl text-white hover:text-[#1FB8D2] transition-colors py-4"
              onClick={closeMobileMenu}
            >
              Portafolio
            </a>
            <a 
              href="#contact" 
              className="text-3xl text-white hover:text-[#1FB8D2] transition-colors py-4"
              onClick={closeMobileMenu}
            >
              Contacto
            </a>
          </div>

          <div className="pb-16 flex justify-center space-x-8">
            <a href="#" className="text-white hover:text-[#1FB8D2] transition-colors" onClick={closeMobileMenu}>
              <svg className="w-8 h-8" fill="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                <path d="M22 12c0-5.523-4.477-10-10-10S2 6.477 2 12c0 4.991 3.657 9.128 8.438 9.878v-6.987h-2.54V12h2.54V9.797c0-2.506 1.492-3.89 3.777-3.89 1.094 0 2.238.195 2.238.195v2.46h-1.26c-1.243 0-1.63.771-1.63 1.562V12h2.773l-.443 2.89h-2.33v6.988C18.343 21.128 22 16.991 22 12z" />
              </svg>
            </a>
